import React, { useState } from "react";
import "./Dictionary.css";
import axios from "axios";
import Definitions from "./Definitions";
export default function Dictionary() {
  let [keyword, setKeyword] = useState("");
  let [definitions, setDefinitions] = useState(null);
  let [loaded, setLoaded] = useState(false);

  function handleResponse(response) {
    setDefinitions(response.data[0]);
  }

  function handleSubmit(event) {
    event.preventDefault();
    search();
  }

  function handleKeywordChange(event) {
    setKeyword(event.target.value);
  }

  function search() {
    let url = `https://api.dictionaryapi.dev/api/v2/entries/en_US/${keyword}`;
    axios.get(url).then(handleResponse);
  }


  if (loaded) {
    return (
      <div className="Dictionary">
        <h1>ðŸŒŠ Dictionary App</h1>
        <section>
          <form onSubmit={handleSubmit}>
            <input
              type="search"
              placeholder="Type a word, e.g. sea, seashore, shells, ocean"
              onChange={handleKeywordChange}
            />
          </form>
        </section>
        <Definitions definitions={definitions} />
      </div>
    );
  } else {
    loaded();
    return "Loading...";
  }
}
